INSERT INTO `news` (`id`, `id_product`, `title`, `description`, `content`, `image`, `create_date`) VALUES
(1, 32, 'Khuyến mại ưu đãi tốt nhất cho những ai đang dùng Iphone 8 Plus', 'Trải qua 13 năm, công nghệ đã giúp cho iphone càng ngày thông minh hơn', 'dfdfdf dfdfd dfdf df', 'https://kenh14cdn.com/thumb_w/620/203336854389633024/2020/12/21/do-cong-nghe-aic-2020-2-1608520812758877807114.jpg', '13/03/2021'),
(4, 0, '  Nhanh tay sắm OPPO Reno4 đang giảm sốc 1 triệu đồng, máy có bộ nhớ trong 128GB, 4 camera sau mà giá quá hời', 'Tin vui cho tín đồ yêu thích chụp ảnh, đặc biệt là chụp selfie, bởi mẫu điện thoại OPPO Reno4 với bộ nhớ trong khủng 128GB được giảm giá khủng 1 triệu kèm trả góp 0% giữa tháng 3 này. Cơ hội có một không hai, sắm được điện thoại ngon với giá tiết kiệm.', '<p><a href=\"https://www.thegioididong.com/tin-tuc/profile/5755350\"><img height=\"20\" src=\"https://graph.facebook.com/1456497354672508/picture\" width=\"20\" />Minh Vũ</a>&nbsp;8 giờ trước</p>\r\n\r\n<p><img alt=\"OPPO Reno4\" src=\"https://cdn.tgdd.vn/Files/2021/03/02/1332003/opporeno4tim-16_800x450.jpg\" title=\"OPPO Reno4\" /></p>\r\n\r\n<h2>Tin vui cho t&iacute;n đồ y&ecirc;u th&iacute;ch chụp ảnh, đặc biệt l&agrave; chụp selfie, bởi mẫu điện thoại&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-reno4\" target=\"_blank\" title=\"Điện thoại OPPO Reno4\" type=\"Điện thoại OPPO Reno4\">OPPO Reno4</a>&nbsp;với bộ nhớ trong khủng 128GB được giảm gi&aacute; khủng 1 triệu k&egrave;m trả g&oacute;p 0% giữa th&aacute;ng 3 n&agrave;y. Cơ hội c&oacute; một kh&ocirc;ng hai, sắm được&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"điện thoại\" type=\"điện thoại\">điện thoại</a>&nbsp;ngon với gi&aacute; tiết kiệm.</h2>\r\n\r\n<p>Thời gian &aacute;p dụng từ&nbsp;<strong>11/3/2021 đến 31/3/2021.</strong></p>\r\n\r\n<p><strong>Lưu &yacute;</strong>: Khuyến m&atilde;i c&oacute; thể kết th&uacute;c sớm nếu hết số lượng.</p>\r\n\r\n<p><strong>OPPO Reno4 sở hữu những điểm nổi bật n&agrave;o m&agrave; bạn n&ecirc;n sở hữu?</strong></p>\r\n\r\n<p><img alt=\"OPPO Reno4\" src=\"https://cdn.tgdd.vn/Files/2020/08/28/1284705/opporeno4tim-18-copy_800x450.jpg\" /></p>\r\n\r\n<p>OPPO Reno4 c&oacute; một vẻ ngo&agrave;i bắt mắt, khi được trang bị m&agrave;n h&igrave;nh AMOLED k&iacute;ch thước 6.4 inch độ ph&acirc;n giải Full HD+, viền m&agrave;n h&igrave;nh rất mỏng. Hơn nữa m&agrave;n h&igrave;nh của thiết bị c&ograve;n hỗ trợ hiển thị HDR10+, đi c&ugrave;ng cảm biến v&acirc;n tay nh&uacute;ng b&ecirc;n dưới m&agrave;n h&igrave;nh.</p>\r\n\r\n<p>Nằm ở mặt lưng,&nbsp;OPPO Reno4 sở hữu bộ bốn camera, trong đ&oacute; c&oacute; camera ch&iacute;nh 48 MP khẩu độ F/1.7, camera g&oacute;c si&ecirc;u rộng 8 MP khẩu độ F/2.2, camera độ s&acirc;u 2 MP khẩu độ F/2.4 v&agrave; camera mono 2 MP đặt song song với đ&egrave;n flash LED.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, OPPO Reno4 c&ograve;n sở hữu camera selfie k&eacute;p với camera ch&iacute;nh 32 MP v&agrave; camera phụ, cho h&igrave;nh ảnh chụp được chi tiết v&agrave; đẹp mắt. OPPO Reno4 hứa hẹn hiệu năng ấn tượng nhờ chip xử l&yacute; Snapdragon 720G với 8 nh&acirc;n, đồ họa Adreno 618, RAM 8 GB v&agrave; bộ nhớ trong 128 GB.</p>\r\n', 'https://cdn.tgdd.vn/Files/2021/03/02/1332003/opporeno4tim-16_800x450.jpg', '10/03/2021'),
(5, 0, '  Galaxy Note 10+ lên đời One UI 3.1 mới nhất, cùng chip Exynos 9825 tạo nên hiệu năng ấn tượng, giá đang giảm hấp dẫn', 'Đã được lên đời One UI 3.1 mới nhất mà Galaxy Note 10+ còn được giảm sốc lên đến 1.5 triệu đồng. Một cơ hội tốt để bạn sở hữu chiếc flagship đến từ thương hiệu nổi tiếng Samsung. Khuyến mãi kéo dài đến hết tháng 3 nên bạn tranh thủ hốt sớm nhé!', '<p><strong>Galaxy Note 10+ sở hữu thiết kế c&ugrave;ng th&ocirc;ng số ấn tượng, chắc chắn bạn n&ecirc;n sở hữu!</strong></p>\r\n\r\n<p><img alt=\"Galaxy Note 10+\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-20.jpg\" /></p>\r\n\r\n<p>Galaxy Note 10+ sở hữu m&agrave;n h&igrave;nh Dynamic AMOLED k&iacute;ch thước lớn l&ecirc;n đến 6.8 inch độ ph&acirc;n giải 2K+ sắc n&eacute;t, hỗ trợ hiển thị HDR10+.</p>\r\n\r\n<p>Nằm nổi bật tr&ecirc;n mặt lưng,&nbsp;Galaxy Note 10+ c&oacute; cụm ba camera chất lượng, c&oacute; c&ugrave;ng độ ph&acirc;n giải 12 MP, đ&oacute; l&agrave; camera ch&iacute;nh, camera g&oacute;c si&ecirc;u rộng v&agrave; camera tele. B&ecirc;n cạnh đ&oacute;&nbsp;Galaxy Note 10+ c&ograve;n được trang bị th&ecirc;m cảm biến 3D ToF phục vụ cho thực tế ảo tăng cường.</p>\r\n\r\n<p>Galaxy Note 10+ hứa hẹn hiệu năng ấn tượng nhờ sở hữu chip xử l&yacute; Exynos 9825 với 8 l&otilde;i hiệu năng, RAM lớn 12 GB v&agrave; bộ nhớ trong 256 GB. Đặc biệt&nbsp;Galaxy Note 10+ c&ograve;n đi k&egrave;m b&uacute;t thần th&aacute;nh S Pen hỗ trợ tối đa cho c&ocirc;ng việc.</p>\r\n', 'https://cdn.tgdd.vn/Files/2021/03/09/1333871/galaxynote10plus_800x450.jpg', '10/03/2021'),
(6, 0, '  Tai nghe AirPods đang giảm lớn đến 30%, các fan \'Táo\' còn đợi gì nữa', 'Các iFan đã biết gì chưa, hiện tại tai nghe AirPods đang giảm lớn lên đến 30% khi đặt mua online. Cơ hội để các bạn có thể rước về cho mình tai nghe sang chảnh của Apple với giá tốt nhất mà chả cần phải đi đâu xa cả.', '<h3><strong>Tai nghe AirPods Pro</strong></h3>\r\n\r\n<p>Tai nghe n&agrave;y sở hữu hiết kế in-ear ho&agrave;n to&agrave;n mới v&agrave; độc đ&aacute;o. Cũng c&oacute; hỗ trợ sạc nhanh, cho thời gian sử dụng đến 1 giờ chỉ với 5 ph&uacute;t sạc. Ngo&agrave;i ra, hộp sạc cũng hỗ trợ sạc kh&ocirc;ng d&acirc;y chuẩn Qi, tiện lợi khi sạc lại. Đặc biệt, sản phẩm n&agrave;y được&nbsp;t&iacute;ch hợp c&ocirc;ng nghệ chống ồn chủ động (Active Noise Cancellation) v&agrave; trang bị chuẩn chống nước IPX4.</p>\r\n\r\n<h3><strong>Tai nghe AirPods 2</strong></h3>\r\n\r\n<p>Tai nghe mang ng&ocirc;n ngữ thiết kế đơn giản, thời trang v&agrave; nhỏ gọn c&ugrave;ng con chip H1 ho&agrave;n to&agrave;n mới, cho tốc độ kết nối, chuyển đổi giữa c&aacute;c thiết bị nhanh ch&oacute;ng. Thời lượng sử dụng l&ecirc;n đến 3 giờ khi nghe nhạc li&ecirc;n tục chỉ với 1 lần sạc đầy hoặc&nbsp;c&oacute; thể d&ugrave;ng được l&ecirc;n đến 24 giờ sử dụng song song với hộp sạc. Đ&atilde; thế c&ograve;n được t&iacute;ch hợp c&ocirc;ng nghệ sạc nhanh hiện đại.</p>\r\n', 'https://cdn.tgdd.vn/Files/2021/03/09/1333836/taingheairpodsprosackhongdayapplemwp22_800x450.jpg', '09/03/2021'),
(7, 0, '  Xiaomi Mi 10S ra mắt: Màn hình 90Hz, camera khủng 108MP, chip mới Snapdragon 870, giá khởi điểm trên 11 triệu đồng', 'Xiaomi đã chính thức cho ra mắt Xiaomi Mi 10S, được coi như một sự bổ sung mới nhất cho dòng sản phẩm Mi 10 đã ra mắt từ năm ngoái. Xiaomi Mi 10S có màn hình đục lỗ, camera chính độ phân giải lớn 108 MP và dùng chip xử lý Snapdragon 870 mới.', '<p><img alt=\"Xiaomi Mi 10S ra mắt\" src=\"https://cdn.tgdd.vn/Files/2021/03/10/1334095/xiaomi-mi-10s-11_800x450.jpg\" title=\"Xiaomi Mi 10S ra mắt\" /></p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Xiaomi\" type=\"Xiaomi\">Xiaomi</a>&nbsp;đ&atilde; ch&iacute;nh thức cho ra mắt&nbsp;<a href=\"https://www.thegioididong.com/dtdd/xiaomi-mi-10s\" target=\"_blank\" title=\"Xiaomi Mi 10S\" type=\"Xiaomi Mi 10S\">Xiaomi Mi 10S</a>, được coi như một sự bổ sung mới nhất cho d&ograve;ng sản phẩm&nbsp;<a href=\"https://www.thegioididong.com/dtdd/xiaomi-mi-10\" target=\"_blank\" title=\"Mi 10\" type=\"Mi 10\">Mi 10</a>&nbsp;đ&atilde; ra mắt từ năm ngo&aacute;i. Xiaomi Mi 10S c&oacute; m&agrave;n h&igrave;nh đục lỗ, camera ch&iacute;nh độ ph&acirc;n giải lớn 108 MP v&agrave; d&ugrave;ng chip xử l&yacute; Snapdragon 870 mới.</h2>\r\n\r\n<p>Cụ thể, Xiaomi Mi 10S sở hữu m&agrave;n h&igrave;nh AMOLED cong k&iacute;ch thước 6.67 inch độ ph&acirc;n giải Full HD+ (2.340 x 1.080 px), hỗ trợ tốc độ l&agrave;m mới khung h&igrave;nh 90 Hz, hiển thị HDR10+, đạt độ s&aacute;ng tối đa 1.120 nits, tỷ lệ khung h&igrave;nh 19.5:9 v&agrave; được bảo vệ bởi k&iacute;nh cường lực Gorilla Glass 5.</p>\r\n\r\n<p><img alt=\"Xiaomi Mi 10S\" src=\"https://cdn.tgdd.vn/Files/2021/03/10/1334095/xiaomi-mi-10s-7_800x450.jpg\" title=\"Xiaomi Mi 10S \" /></p>\r\n\r\n<p>Nằm tr&ecirc;n mặt lưng,&nbsp;Xiaomi Mi 10S c&oacute; thiết lập bốn camera với camera ch&iacute;nh 108 MP khẩu độ F/1.69 hỗ trợ OIS, camera g&oacute;c si&ecirc;u rộng 13 MP khẩu độ F/2.4, camera độ s&acirc;u 2 MP khẩu độ F/2.4 v&agrave; camera macro 2 MP khẩu độ F/2.4.</p>\r\n\r\n<p><img alt=\"Xiaomi Mi 10S\" src=\"https://cdn.tgdd.vn/Files/2021/03/10/1334095/xiaomi-mi-10s-1_800x450.jpg\" title=\"Xiaomi Mi 10S\" /></p>\r\n\r\n<p>Nằm trong nốt ruồi tr&ecirc;n m&agrave;n h&igrave;nh,&nbsp;Xiaomi Mi 10S c&oacute; camera 20 MP phục vụ cho nhu cầu chụp ảnh&nbsp;selfie v&agrave; video call.</p>\r\n\r\n<p>Xiaomi Mi 10S hứa hẹn hiệu năng cao nhờ sở hữu chip Snapdragon 870 mới, đi k&egrave;m đồ họa Adreno 650, bộ nhớ RAM LPDDR5 l&ecirc;n đến 12 GB v&agrave; bộ nhớ lưu trữ UFS 3.0 l&ecirc;n đến 256 GB.</p>\r\n\r\n<p><img alt=\"Xiaomi Mi 10S\" src=\"https://cdn.tgdd.vn/Files/2021/03/10/1334095/xiaomi-mi-10s-3_800x450.jpg\" title=\"Xiaomi Mi 10S\" /></p>\r\n\r\n<p>Xiaomi Mi 10S lấy năng lượng từ vi&ecirc;n pin dung lượng 4.780 mAh, đồng thời hỗ trợ sạc nhanh c&oacute; d&acirc;y 33W, kh&ocirc;ng d&acirc;y 30W v&agrave; sạc ngược 10W. Cũng giống như&nbsp;<a href=\"https://www.thegioididong.com/dtdd/xiaomi-mi-11\" target=\"_blank\" title=\"XIAOMI MI 11\" type=\"XIAOMI MI 11\">Mi 11 5G</a>, Xiaomi cho kh&aacute;ch h&agrave;ng 2 sự lựa chọn c&oacute; hoặc kh&ocirc;ng lấy&nbsp;<a href=\"https://www.thegioididong.com/adapter-sac\" target=\"_blank\" title=\"củ sạc\" type=\"củ sạc\">củ sạc</a>&nbsp;đi k&egrave;m, gi&aacute; kh&ocirc;ng đổi.</p>\r\n\r\n<p><img alt=\"Xiaomi Mi 10S\" src=\"https://cdn.tgdd.vn/Files/2021/03/10/1334095/xiaomi-mi-10s-2_800x450.jpg\" title=\"Xiaomi Mi 10S\" /></p>\r\n\r\n<p>Một số t&iacute;nh năng v&agrave; trang bị kh&aacute;c của&nbsp;Xiaomi Mi 10S: Loa Harman Kardon, cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh, hệ thống l&agrave;m m&aacute;t bằng chất lỏng VC v&agrave; hỗ trợ mạng 5G.</p>\r\n\r\n<p><img alt=\"Xiaomi Mi 10S\" src=\"https://cdn.tgdd.vn/Files/2021/03/10/1334095/xiaomi-mi-10s-8_800x450.jpg\" title=\"Xiaomi Mi 10S\" /></p>\r\n\r\n<p>Xiaomi Mi 10S được b&aacute;n tại Trung Quốc với gi&aacute; như sau:</p>\r\n\r\n<ul>\r\n	<li>RAM 8 GB + ROM 128 GB gi&aacute; 3.299 RMB (11.7 triệu đồng).</li>\r\n	<li>RAM 8 GB + ROM 256 GB gi&aacute; 3.499 RMB (12.4 triệu đồng).</li>\r\n	<li>RAM 12 GB + ROM 256 GB gi&aacute; 3.799 RMB (13.5 triệu đồng).</li>\r\n</ul>\r\n', 'https://cdn.tgdd.vn/Files/2021/03/10/1334095/xiaomi-mi-10s-11_800x450.jpg', '08/03/2021'),
(8, 302, 'Lựa chọn ngay ưu đãi hấp dẫn mới khi mua combo tại shopdienthoai ', 'Lựa chọn ngay một số ưu đãi như tai nghe ipod,đồng hồ mipad và một số ưu đãi khác trong siêu thị', 'dsdsdsds', 'ưdwd', '20/11/2021'),
(9, 1, 'Lựa chọn ngay một con số may mắn nhất khi đến và mua hàng tại siêu thị thegioidienthoai', 'Sự kiện bốc thăm trúng thưởng hot nhất vừa mới được diễn ra tại siêu thị,hãy nhanh tay lựa chọn cho mình một con số may mắn nhất để nhận các quà tặng có giá trị lớn tại siêu thị', 'sđsad\r\nád', 'sd', '22/10/2021'),
(10, 302, 'Nhân dịp tri ân khách hàng,ban quản lí sản phẩm đơn hàng ra sự kiện giảm giá 45% tất cả các sản phẩm', 'Còn chần chừ gì nữa,nhân dịp ưu đãi giảm giá hãy đến ngay tại siêu thị thôi nào', 'sdsdsds', 'sdsd', '22/03/2021');

